		textPane_1.setText("\u0443 \u041B\u0438\u043D\u043D\u0435\u044F \u043D\u0435 \u0432\u043E\u0437\u043D\u0438\u043A\u0430\u043B, \u0442\u0430\u043A \u043A\u0430\u043A \u043E\u043D \u0441\u0447\u0438\u0442\u0430\u043B, \u0447\u0442\u043E \u0432\u0438\u0434\u043E\u0432 \u0441\u0443\u0449\u0435\u0441\u0442\u0432\u0443\u0435\u0442 \u0441\u0442\u043E\u043B\u044C\u043A\u043E, \u0441\u043A\u043E\u043B\u044C\u043A\u043E \u0441\u043E\u0437\u0434\u0430\u043B \u0438\u0445 \u0411\u043E\u0433.\r\n\u0412 1757 \u0433\u043E\u0434\u0443 \u041A\u0430\u0440\u043B \u041B\u0438\u043D\u043D\u0435\u0439, \u0441\u044B\u043D \u0434\u0435\u0440\u0435\u0432\u0435\u043D\u0441\u043A\u043E\u0433\u043E \u0441\u0432\u044F\u0449\u0435\u043D\u043D\u0438\u043A\u0430, \u043F\u043E\u043B\u0443\u0447\u0438\u043B \u0434\u0432\u043E\u0440\u044F\u043D\u0441\u043A\u0438\u0439 \u0442\u0438\u0442\u0443\u043B, \u044D\u0442\u043E \u0431\u044B\u043B\u043E \u043A\u0440\u0430\u0439\u043D\u0435 \u0440\u0435\u0434\u043A\u043E\u0435 \u044F\u0432\u043B\u0435\u043D\u0438\u0435 \u0434\u043B\u044F \u0442\u043E\u0433\u043E \u0432\u0440\u0435\u043C\u0435\u043D\u0438 \u043D\u0435\u0441\u043C\u043E\u0442\u0440\u044F \u043D\u0430 \u0442\u043E, \u0447\u0442\u043E \u043E\u043D \u0442\u043E\u0433\u0434\u0430 \u0443\u0436\u0435 \u0431\u044B\u043B \u0443\u0447\u0435\u043D\u044B\u043C \u0441 \u043C\u0438\u0440\u043E\u0432\u043E\u0439 \u0438\u0437\u0432\u0435\u0441\u0442\u043D\u043E\u0441\u0442\u044C\u044E. ");
