		JTextPane textPane = new JTextPane();
		textPane.setEditable(false);
		textPane.setText("\u041A\u0430\u0440\u043B \u041B\u0438\u043D\u043D\u0435\u0439 \u0438 \u0435\u0433\u043E \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u044F \u0436\u0438\u0432\u044B\u0445 \u043E\u0440\u0433\u0430\u043D\u0438\u0437\u043C\u043E\u0432.\r\n\u041A\u0430\u0440\u043B \u041B\u0438\u043D\u043D\u0435\u0439 (1707 \u2013 1778 \u0433\u0433) \u2013 \u0441\u0430\u043C\u044B\u0439 \u0438\u0437\u0432\u0435\u0441\u0442\u043D\u044B\u0439 \u0448\u0432\u0435\u0434\u0441\u043A\u0438\u0439 \u0443\u0447\u0435\u043D\u044B\u0439-\u0435\u0441\u0442\u0435\u0441\u0442\u0432\u043E\u0438\u0441\u043F\u044B\u0442\u0430\u0442\u0435\u043B\u044C, \u043F\u043E\u043B\u0443\u0447\u0438\u0432\u0448\u0438\u0439 \u043C\u0438\u0440\u043E\u0432\u0443\u044E \u0438\u0437\u0432\u0435\u0441\u0442\u043D\u043E\u0441\u0442\u044C \u0431\u043B\u0430\u0433\u043E\u0434\u0430\u0440\u044F \u0441\u043E\u0437\u0434\u0430\u043D\u0438\u044E \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u0438 \u0436\u0438\u0432\u044B\u0445 \u043E\u0440\u0433\u0430\u043D\u0438\u0437\u043C\u043E\u0432.\r\n\u0412 1735 \u0433\u043E\u0434\u0443 \u0443\u0432\u0438\u0434\u0435\u043B \u0441\u0432\u0435\u0442 \u0433\u043B\u0430\u0432\u043D\u044B\u0439 \u0442\u0440\u0443\u0434 \u041A\u0430\u0440\u043B\u0430 \u041B\u0438\u043D\u043D\u0435\u044F \u2013 \u00AB\u0421\u0438\u0441\u0442\u0435\u043C\u0430 \u043F\u0440\u0438\u0440\u043E\u0434\u044B\u00BB, \u0433\u0434\u0435 \u0432\u043F\u0435\u0440\u0432\u044B\u0435 \u0431\u044B\u043B\u0438 \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u0446\u0438\u0440\u043E\u0432\u0430\u043D\u044B \u0442\u0440\u0438 \u0440\u0430\u0437\u0434\u0435\u043B\u044C\u043D\u044B\u0445 \u0446\u0430\u0440\u0441\u0442\u0432\u0430 \u2013 \u0440\u0430\u0441\u0442\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0439 \u043C\u0438\u0440, \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0439 \u043C\u0438\u0440 \u0438 \u043C\u0438\u0440 \u043C\u0438\u043D\u0435\u0440\u0430\u043B\u043E\u0432.\r\n\u041A\u0430\u0440\u043B \u041B\u0438\u043D\u043D\u0435\u0439 \u0441\u043E\u0437\u0434\u0430\u043B \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u044E \u0436\u0438\u0432\u044B\u0445 \u043E\u0440\u0433\u0430\u043D\u0438\u0437\u043C\u043E\u0432 \u043D\u0430 \u043E\u0441\u043D\u043E\u0432\u0435 \u0441\u043E\u0431\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u0445 \u043D\u0430\u0431\u043B\u044E\u0434\u0435\u043D\u0438\u0439 \u0438 \u0438\u0441\u0441\u043B\u0435\u0434\u043E\u0432\u0430\u043D\u0438\u0439 \u0441\u0432\u043E\u0438\u0445 \u043F\u0440\u0435\u0434\u0448\u0435\u0441\u0442\u0432\u0435\u043D\u043D\u0438\u043A\u043E\u0432 \u2013 \u0410\u0440\u0438\u0441\u0442\u043E\u0442\u0435\u043B\u044F(384-322 \u0433\u0433 \u0434\u043E \u043D.\u044D.), \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u0440\u0430\u0437\u0434\u0435\u043B\u0438\u043B \u0432\u0441\u0435 \u0436\u0438\u0432\u044B\u0435 \u043E\u0440\u0433\u0430\u043D\u0438\u0437\u043C\u044B \u043D\u0430 2 \u0446\u0430\u0440\u0441\u0442\u0432\u0430 \u2013 \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0435 \u0438 \u0440\u0430\u0441\u0442\u0435\u043D\u0438\u044F, \u0438 \u043E\u0445\u0430\u0440\u0430\u043A\u0442\u0435\u0440\u0438\u0437\u043E\u0432\u0430\u043B 495 \u0432\u0438\u0434\u043E\u0432 \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0445, \u0438 \u0411\u043B\u0430\u0436\u0435\u043D\u043D\u043E\u0433\u043E \u0410\u0432\u0433\u0443\u0441\u0442\u0438\u043D\u0430 (354-430), \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u0446\u0438\u0440\u043E\u0432\u0430\u043B \u0432\u0435\u0441\u044C \u0436\u0438\u0432\u043E\u0439 \u043C\u0438\u0440 \u0432 \u043E\u0442\u043D\u043E\u0448\u0435\u043D\u0438\u0438 \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430 \u2013 \u043F\u043E\u043B\u0435\u0437\u043D\u044B\u0435 \u0434\u043B\u044F \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430, \u0432\u0440\u0435\u0434\u043D\u044B\u0435 \u0434\u043B\u044F \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430 \u0438 \u043B\u0438\u0448\u043D\u0438\u0435. \u0422\u0430\u043A\u0436\u0435 \u043C\u043D\u043E\u0433\u043E \u043F\u043E\u0437\u0436\u0435 \u043E\u0434\u043D\u0443 \u0438\u0437 \u0441\u0430\u043C\u044B\u0445 \u0438\u0437\u0432\u0435\u0441\u0442\u043D\u044B\u0445 \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u0439 \u0440\u0430\u0441\u0442\u0435\u043D\u0438\u0439 \u0441\u0434\u0435\u043B\u0430\u043B \u0438\u0442\u0430\u043B\u044C\u044F\u043D\u0441\u043A\u0438\u0439 \u0431\u043E\u0442\u0430\u043D\u0438\u043A \u0410\u043D\u0434\u0440\u0435\u0430 \u0427\u0435\u0437\u0430\u043B\u044C\u043F\u0438\u043D\u043E (1519 \u2013 1603), \u043A\u043E\u0442\u043E\u0440\u043E\u0433\u043E \u043D\u0430\u0437\u044B\u0432\u0430\u043B\u0438 \u00AB\u0410\u0440\u0438\u0441\u0442\u043E\u0442\u0435\u043B\u0435\u043C XIV \u0432\u0435\u043A\u0430\u00BB, \u0438 \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u043F\u0440\u0435\u0434\u043B\u043E\u0436\u0438\u043B \u043F\u0440\u0438 \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u0438 \u0431\u0440\u0430\u0442\u044C \u043F\u0440\u0438\u0437\u043D\u0430\u043A\u0438, \u0432\u0430\u0436\u043D\u044B\u0435 \u0434\u043B\u044F \u0440\u0430\u0441\u0442\u0435\u043D\u0438\u044F, \u0430 \u043D\u0435 \u0434\u043B\u044F \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430.\r\n\u0412 XVI \u2013 XVIII \u0432 \u0415\u0432\u0440\u043E\u043F\u0435 \u0441\u043E\u0431\u0440\u0430\u043B\u043E\u0441\u044C \u043C\u043D\u043E\u0433\u043E \u043E\u0431\u0440\u0430\u0437\u0446\u043E\u0432 \u043D\u0435\u0438\u0437\u0432\u0435\u0441\u0442\u043D\u044B\u0445 \u0440\u0430\u0441\u0442\u0435\u043D\u0438\u0439 \u0438 \u0437\u0430\u043C\u043E\u0440\u0441\u043A\u0438\u0445 \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0445, \u0447\u0442\u043E \u0432\u044B\u0437\u0432\u0430\u043B\u043E \u043D\u0435\u043E\u0431\u0445\u043E\u0434\u0438\u043C\u043E\u0441\u0442\u044C \u043D\u0430\u0432\u0435\u0441\u0442\u0438 \u043A\u0430\u043A\u043E\u0439-\u0442\u043E \u043F\u043E\u0440\u044F\u0434\u043E\u043A.\r\n\r\n\u041E\u0441\u043D\u043E\u0432\u044B\u0432\u0430\u044F\u0441\u044C \u043D\u0430 \u043F\u043E\u043D\u044F\u0442\u0438\u0438 \u00AB\u0432\u0438\u0434\u00BB, \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u0440\u0430\u0437\u0440\u0430\u0431\u043E\u0442\u0430\u043B \u043F\u0440\u0435\u0434\u0448\u0435\u0441\u0442\u0432\u0435\u043D\u043D\u0438\u043A \u041B\u0438\u043D\u043D\u0435\u044F \u0430\u043D\u0433\u043B\u0438\u0439\u0441\u043A\u0438\u0439 \u043D\u0430\u0442\u0443\u0440\u0430\u043B\u0438\u0441\u0442 \u0414\u0436\u043E\u043D \u0420\u0435\u0439 (1627-1705), \u041A\u0430\u0440\u043B \u041B\u0438\u043D\u043D\u0435\u0439 \u0441\u043E\u0437\u0434\u0430\u043B \u043F\u0440\u0438\u043D\u0446\u0438\u043F \u043D\u0430\u0437\u0432\u0430\u043D\u0438\u044F \u043A\u0430\u0436\u0434\u043E\u0433\u043E \u0432\u0438\u0434\u0430 \u2013 \u0431\u0438\u043D\u0430\u0440\u043D\u0443\u044E (\u0434\u0432\u043E\u0438\u0447\u043D\u0443\u044E) \u0441\u0438\u0441\u0442\u0435\u043C\u0443, \u0433\u0434\u0435 \u043F\u0435\u0440\u0432\u043E\u0435 \u0441\u043B\u043E\u0432\u043E \u2013 \u043D\u0430\u0437\u0432\u0430\u043D\u0438\u0435 \u0440\u043E\u0434\u0430, \u0430 \u0432\u0442\u043E\u0440\u043E\u0435 \u2013 \u0432\u0438\u0434\u043E\u0432\u043E\u0439 \u044D\u043F\u0438\u0442\u0435\u0442.\r\n\r\n\u041A\u0430\u0440\u043B \u041B\u0438\u043D\u043D\u0435\u0439 \u043D\u0430\u0437\u0432\u0430\u043B \u0438 \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u0446\u0438\u0440\u043E\u0432\u0430\u043B \u0432\u0441\u0435 \u0438\u0437\u0432\u0435\u0441\u0442\u043D\u044B\u0435 \u043D\u0430 \u0442\u043E \u0432\u0440\u0435\u043C\u044F \u0432\u0438\u0434\u044B, \u043F\u0440\u043E \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0431\u044B\u043B\u043E \u0441\u043E\u0431\u0440\u0430\u043D\u0430 \u0445\u043E\u0442\u044C \u043A\u0430\u043A\u0430\u044F-\u0442\u043E \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u044F. \u041F\u0440\u0435\u0434\u043B\u043E\u0436\u0435\u043D\u043D\u0430\u044F \u041B\u0438\u043D\u043D\u0435\u0435\u043C \u043A\u043B\u0430\u0441\u0441\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u044F \u0436\u0438\u0432\u044B\u0445 \u043E\u0440\u0433\u0430\u043D\u0438\u0437\u043C\u043E\u0432 \u0432\u044B\u0433\u043B\u044F\u0434\u0435\u043B\u0430 \u0442\u0430\u043A: \u0432\u0438\u0434 (\u043A\u043E\u0442 \u0434\u043E\u043C\u0430\u0448\u043D\u0438\u0439) \u2013 \u0440\u043E\u0434 (\u043A\u043E\u0442) \u2013 \u0441\u0435\u043C\u0435\u0439\u0441\u0442\u0432\u043E (\u043A\u043E\u0448\u0430\u0447\u044C\u0438) \u2013 \u0440\u044F\u0434 (\u0445\u0438\u0449\u043D\u0438\u043A\u0438) \u2013 \u043A\u043B\u0430\u0441\u0441 (\u043C\u043B\u0435\u043A\u043E\u043F\u0438\u0442\u0430\u044E\u0449\u0438\u0435) \u2013 \u0442\u0438\u043F (\u0445\u0440\u0435\u0431\u0435\u0442\u043D\u044B\u0435) \u2013 \u0446\u0430\u0440\u0441\u0442\u0432\u043E (\u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0435).\r\n\r\n\u041F\u0440\u0438\u043C\u0435\u0447\u0430\u0442\u0435\u043B\u044C\u043D\u043E, \u0447\u0442\u043E \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430 \u041B\u0438\u043D\u043D\u0435\u0439 \u043E\u0442\u043D\u0435\u0441 \u043A \u0440\u044F\u0434\u0443 \u043F\u0440\u0438\u043C\u0430\u0442\u043E\u0432 \u0438 \u043D\u0430\u0437\u0432\u0430\u043B \u00AB\u0447\u0435\u043B\u043E\u0432\u0435\u043A \u0440\u0430\u0437\u0443\u043C\u043D\u044B\u0439\u00BB(\u00ABHomo sapiens\u00BB), \u043E\u0441\u043D\u043E\u0432\u044B\u0432\u0430\u044F\u0441\u044C \u043D\u0430 \u0441\u0445\u043E\u0436\u0435\u0441\u0442\u0438 \u0441\u0442\u0440\u043E\u0435\u043D\u0438\u044F \u0442\u0435\u043B, \u0430 \u0432\u043E\u043F\u0440\u043E\u0441 \u043E \u043F\u0440\u043E\u0438\u0441\u0445\u043E\u0436\u0434\u0435\u043D\u0438\u0438 \u0432\u0438\u0434\u043E\u0432 \r\n");
